pragma solidity ^0.4.20;

import "../../tokensystem/interfaces/IToken.sol";
import "../../../authentication/Secured.sol";
import "../../../utility/syncing/Syncable.sol";
import "../../../utility/caching/Cacher.sol";
import "../../../utility/caching/CachedByAddress.sol";
import "../../../utility/metadata/MetaDataContainer.sol";


contract IListing is Secured, Syncable, Cacher, CachedByAddress, MetaDataContainer {

  uint public price = 0; // Price
  uint public stake = 0; // Number of unlocked tokens with potential risk if challenged
  uint public challenges = 0; // Number of unresolved challenges on the listing
  uint[] public challengeIDs; // Array of the different challenge IDs
  uint public challengesStake = 0; // Stake with challenged stake added

  bool public whitelisted = true; // Should the data appear on the listing or not
  address public owner; // Owner of Listing

  /**
  @dev Contructor
  @notice                Sets the address for token
  @param _owner          Address of the owner
  @param _price          Price for the listing
  @param _stakeAmount    Amount staked for the listing
  @param _gateKeeper     Address of the gatekeeper
  */
  function IListing(
    address _owner,
    uint _price,
    uint _stakeAmount,
    address _gateKeeper
  )
    Secured(_gateKeeper)
    CachedByAddress(this)
    public
  {
    owner = _owner;
    price = _price;
    stake = _stakeAmount;
  }

  /**
  * implementation of setters
  */
  function setStake(uint _stake) public;

  function setChallengesStake(uint _totalStake) public;

  function setChallenges(uint _challenges) public;

  function setWhitelisted(bool _whitelisted) public;

  function setPrice(uint _price) public;

  function addChallengeID(uint _challengeID) public;

  function getChallengeIDsLength() public returns (uint length);

  function getChallengeIDAtIndex(uint _index) public returns (uint challengeID);
}
