pragma solidity ^0.4.17;

import "../../../utility/syncing/Syncable.sol";
import "../../../authentication/Secured.sol";
import "../../../utility/caching/CachedByBytes32.sol";
import "../../../utility/caching/Cacher.sol";
import "../../../utility/metadata/MetaDataContainer.sol";


contract IToken is Secured, Syncable, Cacher, CachedByBytes32, MetaDataContainer {

  bytes32 constant public MINT_ROLE = "MINT_ROLE";
  bytes32 constant public BURN_ROLE = "BURN_ROLE";

  bytes32 public name;
  uint8 public decimals;

  event Transfer(address indexed from, address indexed to, uint256 value);
  event TransferData(address indexed from, address indexed to, bytes data);
  event Mint(address indexed to, uint256 amount);
  event Burn(address indexed from, uint256 amount);
  event Approval(address indexed owner, address indexed spender, uint256 value);

  function IToken(
    bytes32 _name,
    uint8 _decimals,
    address _registry,
    address _gateKeeper
  )
    Secured(_gateKeeper)
    CachedByBytes32(_name, _registry)
    public
  {
    name = _name;
    decimals = _decimals;
  }

  function mint(address _to, uint256 _amount) auth(MINT_ROLE) public returns (bool success);

  function mintToRoleRegistry(address _roleRegistryAddress, uint256 _amount) auth(MINT_ROLE) public  returns (bool success);

  function burn(address _from, uint256 _amount) auth(BURN_ROLE) public returns (bool success);

  function burnFromRoleRegistry(address _roleRegistryAddress, uint256 _amount) auth(BURN_ROLE) public  returns (bool success);

  function transfer(address _to, uint256 _value) public returns (bool success);

  function transferWithData(address _to, uint256 _value, bytes _data) public returns (bool success);

  function transferFrom (address _from, address _to, uint256 _value ) public returns (bool success);

  function transferFromWithData(
    address _from,
    address _to,
    uint256 _value,
    bytes _data
  )
    public
    returns (bool success);

  function approve(address _spender, uint256 _value) public returns (bool);

  function allowance(address _owner, address _spender) public view returns (uint256 remaining);

  function increaseApproval (address _spender, uint _addedValue) public returns (bool success);

  function decreaseApproval (address _spender, uint _subtractedValue) public returns (bool success);

  function balanceOf(address _owner) public view returns (uint256 balance);

}
