pragma solidity ^0.4.20;

import "./interfaces/IListing.sol";


contract Listing is IListing {

  bytes32 constant public BLACKLIST_ROLE = "BLACKLIST_ROLE";

  event Blacklisted(address listing);
  event StakeSet(address listing, uint stake);
  event ChallengesStakeSet(address listing, uint stake);
  event ChallengesSet(address listing, uint challenges);
  event WhitelistedSet(address listing, bool whitelisted);
  event PriceSet(address listing, uint price);
  event ChallengeIDSet(address listing, uint challengeID);

  /**
  @dev Contructor
  @notice                Sets the address for token
  @param _owner          Address of the owner
  @param _price          Price for the listing
  @param _stakeAmount    Amount staked for the listing
  @param _gateKeeper     Address of the gatekeeper
  */
  function Listing(
    address _owner,
    uint _price,
    uint _stakeAmount,
    address _gateKeeper
  )
    IListing(_owner, _price, _stakeAmount, _gateKeeper)
    public
  {}

  /**
  @notice         Sets whitelisted to false: the listing should not appear anymore
  */
  function blacklist() auth(BLACKLIST_ROLE) public {
    whitelisted = false;
    Blacklisted(this);
  }

  /**
  * implementation of setters
  */

  /**
  @notice         Sets the stake
  @param _stake   Stake
  */
  function setStake(uint _stake) public {
    stake = _stake;
    StakeSet(this, _stake);
  }

  /**
  @notice                   Sets the amount staked for all challenged
  @param _challengesStake   Amount staked for all challenges
  */
  function setChallengesStake(uint _challengesStake) public {
    challengesStake = _challengesStake;
    ChallengesStakeSet(this, _challengesStake);
  }

  /**
  @notice              Sets the muber of challenges
  @param _challenges   Number of challenges
  */
  function setChallenges(uint _challenges) public {
    challenges = _challenges;
    ChallengesSet(this, _challenges);
  }

  /**
  @notice               Sets whether or not the listing is whitelisted
  @param _whitelisted   Whitelisted
  */
  function setWhitelisted(bool _whitelisted) public {
    whitelisted = _whitelisted;
    WhitelistedSet(this, _whitelisted);
  }

  /**
  @notice         Sets the price
  @param _price   Price
  */
  function setPrice(uint _price) public {
    price = _price;
    PriceSet(this, _price);
  }

  /**
  @notice               Adds a challenge ID in the array
  @param _challengeID   Challenge ID
  */
  function addChallengeID(uint _challengeID) public {
    challengeIDs.push(_challengeID);
    ChallengeIDSet(this, _challengeID);
  }

  /**
  * implementation of getters
  */

  /**
  @notice         Get length of challenge ID array
  */
  function getChallengeIDsLength() public returns (uint length) {
    return challengeIDs.length;
  }

  /**
  @notice         Get challenge ID at index in array
  @param _index   Index
  */
  function getChallengeIDAtIndex(uint _index) public returns (uint challengeID) {
    return challengeIDs[_index];
  }
}

