pragma solidity ^0.4.17;

import "./GateKeeper.sol";


contract Secured {

  GateKeeper public gateKeeper;

  modifier auth(bytes32 _role) {
    require(canPerform(msg.sender, _role));
    _;
  }

  function Secured(address _gateKeeper) public {
    gateKeeper = GateKeeper(_gateKeeper);
  }

  function canPerform(address _sender, bytes32 _role) internal constant returns (bool) {
    return address(gateKeeper) == 0 || gateKeeper.hasPermission(_sender, address(this), _role);
  }

}
