pragma solidity ^0.4.24;

import "openzeppelin-solidity/contracts/math/SafeMath.sol";
import "./interfaces/IToken.sol";
import "../../authentication/RoleRegistry.sol";


contract Token is IToken {
  using SafeMath for uint256;

  struct TokenAccount {
    uint256 balance;
    uint256 firstTransaction;
  }

  mapping(address => TokenAccount) balances;
  address[] tokenHolders;
  mapping (address => mapping (address => uint256)) internal allowed;
  uint256 public totalSupply;

  constructor(
    bytes32 _name,
    uint8 _decimals,
    address _gateKeeper
  )
    IToken(_name, _decimals, _gateKeeper)
    public
  {}

  function mint(address _to, uint256 _amount) public auth(MINT_ROLE) returns (bool success) {
    if (balances[_to].firstTransaction == 0) {
      balances[_to].firstTransaction = now;
      tokenHolders.push(_to);
    }
    totalSupply = totalSupply.add(_amount);
    balances[_to].balance = balances[_to].balance.add(_amount);
    emit Mint(_to, _amount);
    emit Transfer(0x0, _to, _amount);
    return true;
  }

  function mintToRoleRegistry(address _roleRegistryAddress, uint256 _amount) public auth(MINT_ROLE) returns (bool success) {
    RoleRegistry roleRegistry = RoleRegistry(_roleRegistryAddress);
    uint256 numberOfUsers = roleRegistry.getIndexLength();
    for (uint256 counter = 0; counter < numberOfUsers; counter++) {
      address _holder;
      bool _hasRole;
      ( _holder,_hasRole ) = roleRegistry.getByIndex(counter);
      if (_hasRole) {
        mint(_holder, _amount);
      }
    }
    return true;
  }

  function burn(address _from, uint256 _amount) public auth(BURN_ROLE) returns (bool success) {
    require(_amount > 0, "amount hsould be bigger than 0");
    require(_amount <= balances[_from].balance, "amount should be less than balance");
    balances[_from].balance = balances[_from].balance.sub(_amount);
    totalSupply = totalSupply.sub(_amount);
    emit Burn(_from, _amount);
    emit Transfer(_from, 0x0, _amount);
    return true;
  }

  function burnFromRoleRegistry(address _roleRegistryAddress, uint256 _amount) public auth(BURN_ROLE) returns (bool success) {
    RoleRegistry roleRegistry = RoleRegistry(_roleRegistryAddress);
    uint256 numberOfUsers = roleRegistry.getIndexLength();
    for (uint256 counter = 0; counter < numberOfUsers; counter++) {
      address _holder;
      bool _hasRole;
      ( _holder,_hasRole ) = roleRegistry.getByIndex(counter);
      if (_hasRole) {
        burn(_holder, _amount);
      }
    }
    return true;
  }

  function transfer(address _to, uint256 _value) public returns (bool success) {
    require(_to != address(0), "to should not be 0");
    require(_value <= balances[msg.sender].balance, "value should be less than balance");

    if (balances[_to].firstTransaction == 0) {
      balances[_to].firstTransaction = now;
      tokenHolders.push(_to);
    }

    balances[msg.sender].balance = balances[msg.sender].balance.sub(_value);
    balances[_to].balance = balances[_to].balance.add(_value);
    emit Transfer(msg.sender, _to, _value);
    return true;
  }

  function transferWithData(address _to, uint256 _value, bytes _data) public returns (bool success) {
    require(transfer(_to, _value),"transfer failed");
    emit TransferData(msg.sender, _to, _data);
    return true;
  }

  function transferFrom (address _from, address _to, uint256 _value ) public returns (bool success) {
    require(_to != address(0), "to should not be null");
    require(_value <= balances[_from].balance, "value should be less than balance");
    require(_value <= allowed[_from][msg.sender], "value should be less than allowance");

    if (balances[_to].firstTransaction == 0) {
      balances[_to].firstTransaction = now;
      tokenHolders.push(_to);
    }

    balances[_from].balance = balances[_from].balance.sub(_value);
    balances[_to].balance = balances[_to].balance.add(_value);
    allowed[_from][msg.sender] = allowed[_from][msg.sender].sub(_value);
    emit Transfer(_from, _to, _value);
    return true;
  }

  function transferFromWithData(
    address _from,
    address _to,
    uint256 _value,
    bytes _data
  ) public returns (bool success)
  {
    require(transferFrom(_from, _to, _value), "transfer failed");
    emit TransferData(_from, _to, _data);
    return true;
  }

  /**
   * @dev Approve the passed address to spend the specified amount of tokens on behalf of msg.sender.
   *
   * Beware that changing an allowance with this method brings the risk that someone may use both the old
   * and the new allowance by unfortunate transaction ordering. One possible solution to mitigate this
   * race condition is to first reduce the spender's allowance to 0 and set the desired value afterwards:
   * https://github.com/ethereum/EIPs/issues/20#issuecomment-263524729
   * @param _spender The address which will spend the funds.
   * @param _value The amount of tokens to be spent.
   */
  function approve(address _spender, uint256 _value) public returns (bool) {
    allowed[msg.sender][_spender] = _value;
    emit Approval(msg.sender, _spender, _value);
    return true;
  }

  /**
   * @dev Function to check the amount of tokens that an owner allowed to a spender.
   * @param _owner address The address which owns the funds.
   * @param _spender address The address which will spend the funds.
   * @return A uint256 specifying the amount of tokens still available for the spender.
   */
  function allowance(address _owner, address _spender) public view returns (uint256 remaining) {
    return allowed[_owner][_spender];
  }

  /**
   * @dev approve should be called when allowed[_spender] == 0. To increment
   * allowed value is better to use this function to avoid 2 calls (and wait until
   * the first transaction is mined)
   * From MonolithDAO Token.sol
   */
  function increaseApproval (address _spender, uint _addedValue) public returns (bool success) {
    allowed[msg.sender][_spender] = allowed[msg.sender][_spender].add(_addedValue);
    emit Approval(msg.sender, _spender, allowed[msg.sender][_spender]);
    return true;
  }

  function decreaseApproval (address _spender, uint _subtractedValue) public returns (bool success) {
    uint oldValue = allowed[msg.sender][_spender];
    if (_subtractedValue > oldValue) {
      allowed[msg.sender][_spender] = 0;
    } else {
      allowed[msg.sender][_spender] = oldValue.sub(_subtractedValue);
    }
    emit Approval(msg.sender, _spender, allowed[msg.sender][_spender]);
    return true;
  }

  /**
  * @dev Gets the balance of the specified address.
  * @param _owner The address to query the the balance of.
  * @return An uint256 representing the amount owned by the passed address.
  */
  function balanceOf(address _owner) public view returns (uint256 balance) {
    return balances[_owner].balance;
  }

  function updateMetaData(string ipfsHash) public auth(UPDATE_METADATA_ROLE) {
    super.updateMetaData(ipfsHash);
  }
}

