pragma solidity ^0.4.20;

import "./interfaces/IListing.sol";
import "./interfaces/IListingFactory.sol";
import "../tokensystem/interfaces/IToken.sol";
import "../../authentication/Secured.sol";
import "../../utility/syncing/Syncable.sol";
import "../../utility/caching/Cacher.sol";
import "../../utility/metadata/MetaDataContainer.sol";
import "zeppelin-solidity/contracts/math/SafeMath.sol";


contract TokenCuratedRegistry is Secured, Syncable, Cacher {
  using SafeMath for uint256;

  bytes32 constant public WITHDRAW_FUNDS_ROLE = "WITHDRAW_FUNDS_ROLE";
  bytes32 constant public CURATE_CHALLENGE_ROLE = "CURATE_CHALLENGE_ROLE";
  bytes32 constant public CHANGE_SETTINGS_ROLE = "CHANGE_SETTINGS_ROLE";

  // ------
  // EVENTS
  // ------

  event Enlisted(address listing, uint stake, uint price);
  event Challenged(address listing, uint stake, uint challengeID);
  event Unlisted(address listing);
  event ChallengeApproved(address listing);
  event ChallengeDenied(address listing);
  event Increased(address listing, uint increasedBy, uint newStake);
  event Decreased(address listing, uint decreasedBy, uint newStake);
  event Withdrawn(address by, uint balance);
  event MinEnlistAmountChanged(uint value);
  event MinChallengeAmountChanged(uint value);
  event CuratorPercentageChanged(uint value);

  struct Challenge {
    address challenger;     // Owner of Challenge
    bool resolved;          // Indication of if challenge is resolved
    uint stake;             // Number of tokens at risk for either party during challenge
    address listing;        // Listing this challenge was added to
  }

  // Maps listingHashes to associated listing data
  mapping(address => IListing) public listings;
  address[] listingsIndex;

  // Maps challenges to associated challenge data
  mapping(uint => Challenge) public challenges;

  // Global Variables
  IToken public token;
  IListingFactory public listingFactory;
  address gateKeeper;
  uint challengeID = 1; // Keep track of challenges
  // Settings
  uint minEnlistAmount = 0;
  uint minChallengeAmount = 0;
  uint curatorPercentage = 0;

  /**
  @dev Contructor
  @notice                Sets the address for token
  @param _gateKeeper     Address of the gatekeeper
  @param _token          Address of the token
  */
  function TokenCuratedRegistry(
    address _gateKeeper,
    address _token,
    address _listingFactory,
    uint _minEnlistAmount,
    uint _minChallengeAmount,
    uint _curatorPercentage
  )
    Secured(_gateKeeper)
    public
  {
    token = IToken(_token);
    listingFactory = IListingFactory(_listingFactory);

    minEnlistAmount = _minEnlistAmount;
    minChallengeAmount = _minChallengeAmount;
    curatorPercentage = _curatorPercentage;
    gateKeeper = _gateKeeper;
  }

  /**
  @notice                 Allows a seller to list data to be available for selling.
  @notice                 Takes tokens from user.
  @param _stakeAmount     The number of ERC20 tokens a user is willing to potentially stake
  @param _price           Price of the listing
  @param _metadata        Metadata
  */
  function enlist(uint _stakeAmount, uint _price, string _metadata) external {
    // Stake must be above a certain amount
    require(_stakeAmount >= minEnlistAmount);

    // Transfers tokens from user to Registry contract
    require(token.transferFrom(msg.sender, this, _stakeAmount));

    // Add listing to listings
    listingFactory.createListing(
      msg.sender,
      _price,
      _stakeAmount,
      this,
      _metadata
    );
  }

  /**
  @notice                 Listing is added through the factory
  @param _listing         Address of the added listing
  */
  function addListing(address _listing) public {
    IListing listing = IListing(_listing);

    // Add to mapping
    listings[_listing] = listing;
    listingsIndex.push(_listing);

    // Event
    emit Enlisted(_listing, listing.stake(), listing.price());
    invalidateCache(_listing, "", 0);
  }

  /**
  @notice             Allows the owner of a listing to remove the listing from the whitelist
  @notice             Returns all tokens to the owner of the listing
  @param _listing     The listing of a user's listing
  */
  function unlist(address _listing) public {
    IListing listing = listings[_listing];
    require(msg.sender == listing.owner());

    // Cannot exit during ongoing challenge
    require(listing.challenges() == 0);

    // Transfers any remaining balance back to the owner
    if (listing.stake() > 0)
      require(token.transfer(listing.owner(), listing.stake()));

    // Blacklist
    listing.setWhitelisted(false);

    // Event
    emit Unlisted(_listing);
    invalidateCache(_listing, "", 0);
  }

  /**
  @notice             Allows the owner of a listing to increase their unstaked deposit.
  @param _listing     The listing of a user's application/listing
  @param _stakeAmount The number of ERC20 tokens to increase a user's unstaked deposit
  */
  function increase(address _listing, uint _stakeAmount) public {
    IListing listing = listings[_listing];

    require(listing.owner() == msg.sender);
    require(token.transferFrom(msg.sender, this, _stakeAmount));

    listing.setStake(listing.stake().add(_stakeAmount));

    // Event
    emit Increased(_listing, _stakeAmount, listing.stake());
    invalidateCache(_listing, "", 0);
  }

  /**
  @notice             Allows the owner of a listing to decrease their unstaked deposit.
  @notice             The listing keeps its previous status.
  @param _listing     The listing of a user's application/listing
  @param _stakeAmount The number of ERC20 tokens to decrease a user's unstaked deposit
  */
  function decrease(address _listing, uint _stakeAmount) public {
    IListing listing = listings[_listing];

    uint stake = listing.stake();

    require(listing.owner() == msg.sender);
    require(_stakeAmount <= stake);
    require(stake - _stakeAmount >= minEnlistAmount);

    require(token.transfer(msg.sender, _stakeAmount));

    listing.setStake(stake.sub(_stakeAmount));

    // Event
    emit Decreased(_listing, _stakeAmount, listing.stake());
    invalidateCache(_listing, "", 0);
  }

  /**
  @notice             Starts a challenge for a listing.
  @dev                Tokens are taken from the challenger and the data seller's deposit is locked.
  @param _listing     The listing of data.
  @param _stakeAmount The amount the challenger wants to stake.
  */
  function challenge(address _listing, uint _stakeAmount) public {
    require(_stakeAmount >= minChallengeAmount);

    IListing listing = listings[_listing];

    // Takes tokens from challenger
    require(token.transferFrom(msg.sender, this, _stakeAmount));

    // Add challenge to the challenges mapping
    Challenge memory _challenge = Challenge({
      challenger: msg.sender,
      stake: _stakeAmount,
      resolved: false,
      listing: _listing
    });
    challenges[challengeID] = _challenge;

    // Increase the number of challenges on the right listing
    listing.setChallenges(listing.challenges() + 1);
    // Add challengeID to listing
    listing.addChallengeID(challengeID);
    // Add to the challengesStake on a listing
    listing.setChallengesStake(listing.challengesStake().add(_stakeAmount));

    // Event
    emit Challenged(_listing, _stakeAmount, challengeID);
    invalidateCache(_listing, "", 0);
  }

   /**
  @notice             Marks a challenge as resolved.
  @notice             Rewards the winner tokens and either whitelists or de-whitelists the listing.
  @param _listing     A listing with a challenge that is to be resolved
  */
  function approveChallenge(address _listing) auth(CURATE_CHALLENGE_ROLE) public {
    IListing listing = listings[_listing];

    uint challengesStake = listing.challengesStake();

    // Curator (msg.sender) should get 10% of the total sum of all challenge stakes + lising stake
    uint curatorShare = challengesStake.mul((curatorPercentage.div(100)));
    token.transfer(msg.sender, curatorShare);

    // Challengers should get a percentage of the total sum of all challenge stakes + lising stake
    // equal to their share of the total challenge stake
    // Get all challenges that are unresolved and are linked to this listing,
    for (uint i = 0; i < listing.getChallengeIDsLength(); i ++) {
      Challenge memory _challenge = challenges[listing.getChallengeIDAtIndex(i)];

      // Calculate what's left to divide between the challengers
      uint challengerShare = challengesStake.sub(curatorShare);
      // Transfer percentage of challenge
      token.transfer(_challenge.challenger, challengerShare.mul(_challenge.stake.div(challengesStake)));
      // Set challenge as resolved
      challenges[listing.getChallengeIDAtIndex(i)].resolved = true;
    }

    // Take stake from listing
    listing.setStake(0);
    listing.setChallengesStake(0);
    listing.setChallenges(0);

    // Blacklist listing
    listing.setWhitelisted(false);

    // Event
    emit ChallengeApproved(_listing);
    invalidateCache(_listing, "", 0);
  }

   /**
  @notice             Marks a challenge as denied.
  @notice             Rewards the listing seller tokens and de-whitelists the listing.
  @param _listing     A listing with a challenge that is to be resolved
  */
  function denyChallenge(address _listing) auth(CURATE_CHALLENGE_ROLE) public {
    IListing listing = listings[_listing];

    uint challengesStake = listing.challengesStake();

    // Admin (msg.sender) should get 10% of the total sum of all challenge stakes + lising stake
    uint curatorShare = challengesStake.mul(curatorPercentage.div(100));
    token.transfer(msg.sender, curatorShare);

    // Transfer the rest of the stake to the data owner
    token.transfer(listing.owner(), challengesStake.sub(curatorShare));

    // Get all challenges that are unresolved and are linked to this listing,
    for (uint i = 0; i < listing.getChallengeIDsLength(); i ++) {
      // Set challenge as resolved
      challenges[listing.getChallengeIDAtIndex(i)].resolved = true;
    }

    // Remove challenges on the listing
    listing.setChallenges(0);
    listing.setChallengesStake(0);

    // Event
    emit ChallengeDenied(_listing);
    invalidateCache(_listing, "", 0);
  }

  /**
  @notice      Withdraw all the funds from this contract: safety measure
  */
  function withdraw() auth(WITHDRAW_FUNDS_ROLE) public {
    uint balance = address(this).balance;
    token.transfer(msg.sender, address(this).balance);
    emit Withdrawn(msg.sender, balance);
  }

  /**
  @notice                   Sets the minEnlistAmount
  @param _minEnlistAmount   minEnlistAmount
  */
  function setMinEnlistAmount(uint _minEnlistAmount) auth(CHANGE_SETTINGS_ROLE) public {
    minEnlistAmount = _minEnlistAmount;
    emit MinEnlistAmountChanged(_minEnlistAmount);
  }

  /**
  @notice                     Sets the minChallengeAmount
  @param _minChallengeAmount  minChallengeAmount
  */
  function setMinChallengeAmount(uint _minChallengeAmount) auth(CHANGE_SETTINGS_ROLE) public {
    minChallengeAmount = _minChallengeAmount;
    emit MinChallengeAmountChanged(_minChallengeAmount);
  }

  /**
  @notice                   Sets the curatorPercentage
  @param _curatorPercentage curatorPercentage
  */
  function setCuratorPercentage(uint _curatorPercentage) auth(CHANGE_SETTINGS_ROLE) public {
    curatorPercentage = _curatorPercentage;
    emit CuratorPercentageChanged(_curatorPercentage);
  }

  /**
  * implementation of cacher methods
  */
  function invalidateCache(address _cachedAddress, bytes32 /*_cachedBytes32*/, uint256 /*_cachedUint256*/) public {
    emit AddressCacheInvalidated(_cachedAddress);
  }

  /**
  * implementation of syncable methods
  */
  function getIndexLength() public view returns (uint length) {
    length = listingsIndex.length;
  }

  function getByIndex(uint index) public view returns (address key, address contractAddress) {
    return getByKey(listingsIndex[index]);
  }

  function getByKey(address _key) public view returns (address key, address contractAddress) {
    key = address(listings[_key]);
    contractAddress = address(listings[_key]);
  }
}

