pragma solidity ^0.4.22;

import "./interfaces/ITokenRegistry.sol";


contract TokenRegistry is ITokenRegistry {

  constructor(address _gateKeeper) ITokenRegistry(_gateKeeper) public {}

  function addToken(bytes32 _name, address _token) public auth(LIST_TOKEN_ROLE) {
    require(tokens[_name] == address(0x0), "only accept unique names");
    tokens[_name] = IToken(_token);
    tokenIndex.push(_name);
    emit Bytes32CacheInvalidated(_name);
  }

  function getIndexLength() public view returns (uint length) {
    length = tokenIndex.length;
  }

  function getByIndex(uint index) public view returns (bytes32 key, address contractAddress) {
    return getByKey(tokenIndex[index]);
  }

  function getByKey(bytes32 _key) public view returns (bytes32 key, address contractAddress) {
    contractAddress = tokens[_key];
    key = _key;
  }

  function invalidateCache(address /*_cachedAddress*/, bytes32 _cachedBytes32, uint256 /*_cachedUint256*/) public {
    emit Bytes32CacheInvalidated(_cachedBytes32);
  }
}
